import { useState } from 'react';
import Square from './Square';

const Board = () => {
  const [value, setValue] = useState(Array(9).fill(null));

  const handleClick = (i) => {
    //   let newSquare = squares.slice();
    console.log(i);
    setValue('X');
    //   newSquare[i] = 'X';
    //   setSquares(newSquare);
    //   console.log(squares);
  };
  // console.log(squares);

  const renderSquare = (i) => {
    return <Square value={i} onClick={handleClick(i)} />;
  };

  const status = 'Next player: X';

  return (
    <div className="board">
      <div className="status">{status}</div>
      <div className="board-row">
        {/* <Square value={square} /> */}
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="board-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="board-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
    </div>
  );
};

export default Board;
