import Square from './Square';

const Board = () => {
  const [value, setValue] = useState('');

  const renderSquare = (i) => {
    return <Square value={value} onClick={() => setValue('X')} />;
  };
  const status = 'Next player: X';

  return (
    <div className="board">
      <div className="status">{status}</div>
      <div className="board-row">
        <Square value={value} onClick={() => setValue('X')} />
        <Square value />
        {renderSquare()}
        {/* {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)} */}
      </div>
      <div className="board-row">
        <Square value />
        <Square value />
        <Square value />
      </div>
      <div className="board-row">
        <Square value />
        <Square value />
        <Square value />
      </div>
    </div>
  );
};

export default Board;
